import React, { useState } from "react"


function AddBounty(props) {
    const initInputs = {
        firstName: "",
        lastName: "",
        status: true,
        price: 0,
        title: ""
    }
    const [inputs, setInputs] = useState(initInputs)
    console.log(inputs.status)

    function handleChange(e) {
        const { name, value } = e.target
        setInputs(prevInputs => ( {...prevInputs, [name]: value }))
    }

    function handleStatusChange(e) {
        setInputs(prevInputs => ( { [prevInputs.status]: e.target.value }) )

    }

    function handleSubmit(e) {
        e.preventDefault()
        console.log(inputs)
        // post request
        setInputs(initInputs)
    }
    return (
        <form onSubmit={handleSubmit}>

            <input type="text" name="firstName" value={inputs.firstName} onChange={handleChange} placeholder="First Name"/>

            <input type="text" name="lastName" value={inputs.lastName} onChange={handleChange} placeholder="Last Name"/>

            <br />
                <input type="radio"  name="status" value={inputs.status}  onChange={handleStatusChange} checked={inputs.status === true}/>
                <label>Alive</label><br />
                <input type="radio" name="status" value={false} onChange={handleStatusChange} checked={inputs.status === false}/>
                <label>Dead</label>

                <br/>

            <input type="number" name="price" value={inputs.price} onChange={handleChange}/> : Amount
            <br />

            <input type="text" name="title" value={inputs.title} onChange={handleChange} placeholder="Sith or Jedi" /> : Title
            <br />

        <button>Add Bounty</button>
        </form>
    )
}



export default AddBounty
