import React, { useState } from "react"


function AddBounty(props) {
    const initInputs = {
        type: "",
        firstName: "",
        lastName: "",
        status: "",
        price: 0,
        title: ""
    }
    const [inputs, setInputs] = useState(initInputs)

    function handleChange(e) {
        const { name, value } = e.target
        setInputs(prevInputs => ( {...prevInputs, [name]: value }))
    }

    function handleSubmit(e) {
        e.preventDefault()
        // post request
        setInputs(initInputs)
    }
    return (
        <form onSubmit={handleSubmit}>

            <input type="text" name="firstName" value={inputs.firstName} onChange={handleChange} placeholder="First Name"/>

            <input type="text" name="lastName" value={inputs.lastName} onChange={handleChange} placeholder="Last Name"/>
            <br />
            <input type="radio"  name="status" value="alive" checked={true} onChange={handleChange}/>
                <label>Alive</label><br />
                <input type="radio" id="dead" name="status" value="dead" onChange={handleChange}/>
                <label>Dead</label><br/>

            <input type="number" name="price" value={inputs.price} onChange={handleChange} />

            <input type="text" name="title" value={inputs.title} onChange={handleChange} placeholder="Sith or Jedi" />

        <button>Add Bounty</button>
        </form>
    )
}



export default AddBounty
